import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import styled from "styled-components";
import axios from "axios";
import "./pokemonde.css";

const Root = styled.div`
  display: flex;
  flex-direction: row;
  margin: 40px;
  gap: 50px;
  justify-content: center;
`;

const InfoCard = styled.div`
  display: flex;
  flex-direction: column;
  border: 1px solid black;
`;
const divDoMeio = styled.div``;

const ImagemPokemon = styled.img``;

const PokemonDetails = () => {
  const { id } = useParams();
  const [pokemons, setPokemons] = useState([]);

  useEffect(() => {
    axios
      .get(`https://pokeapi.co/api/v2/pokemon/${id}`)
      .then((res) => {
        setPokemons([res.data]);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  const backgroundType = (type) => {
    switch (type) {
      case "normal":
        return "lightblue";
      case "fighting":
        return "red";
      case "flying":
        return "lightblue";
      case "poison":
        return "purple";
      case "ground":
        return "brown";
      case "rock":
        return "brown";
      case "bug":
        return "green";
      case "ghost":
        return "purple";
      case "steel":
        return "grey";
      case "fire":
        return "red";
      case "water":
        return "blue";
      case "grass":
        return "green";
      case "electric":
        return "yellow";
      case "psychic":
        return "pink";
      case "ice":
        return "lightblue";
      case "dragon":
        return "purple";
      case "dark":
        return "purple";
      case "fairy":
        return "pink";
      default:
        return "black";
    }
  };

  const pokemap = pokemons.map((poke) => (
    // <Root>
