import React from "react";
import styled from "styled-components";
import {Link} from 'react-router-dom'



const PokecardContainer = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr 1fr 1fr 1fr;
  grid-gap: 10px;
  margin: 10px;
`;

const PokeImage = styled.div`
    background-color: cornflowerblue;
    border-radius: 50%;
    margin: 10px;
    `;

const Home = (props) => {


  return (

    <PokecardContainer>
      {props.pokemonlist.map((pokemon) => ( 

        <div class="nes-container is-rounded" key={pokemon.name} style={{
          backgroundColor: "#F7DE3F",
          color: "#3860BE",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
        }}>
            <h2>{pokemon.name}</h2>
            <PokeImage>
            <img src={props.buildImgUrl(pokemon.url)} alt={pokemon.name} />
            </PokeImage>
              <button type="button" class="nes-btn is-success" onClick={ () => props.addToPokedex(pokemon)}>Add</button>
              <Link to={`/details/${props.getPokemonId(pokemon.url)}`}>
              <button type="button" class="nes-btn is-primary ">Detalhes</button>              
              </Link>
        {/* </Pokecard> */}
            </div>
      ))}

    </PokecardContainer>
  );
};

export default Home;
