import React from "react";
import { useEffect, useState } from "react";
import axios from "axios";
import styled from "styled-components";
import {Link} from 'react-router-dom'
import ReactPaginate from "react-paginate";

const PokecardContainer = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr 1fr 1fr 1fr;
`;

const Pokecard = styled.div`
  border: 1px solid black;
  margin: 10px;
  padding: 10px;
  display: flex;
  flex-direction: column;
  gap: 10px;
`;

const Home = () => {
  const [pokemonlist, setPokemonlist] = useState([]);
  const [pageCount, setpageCount] = useState(0);

  let limit = 100;


  useEffect(() => {
    axios
      .get(`https://pokeapi.co/api/v2/pokemon?limit=${limit}`)
      .then((res) => {
        setPokemonlist(res.data.results);
        console.log(res.data.results)
        const total = res.headers.get("x-total-count");
        setpageCount(Math.ceil(total / limit));
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  const buildImgUrl = (url) => {
    const id = url.split("/");
    const idx = id.length - 2;
    const imgUrl = `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${id[idx]}.png`;

    return imgUrl;
    };

    const getPokemonId = (url) => {
      const id = url.split("/");
      const idx = id.length - 2;
      const pokemonId = id[idx];
      return pokemonId;
    };

    const handlePageClick = async (data) => {
      console.log(data.selected);

      let currentPage = data.selected + 1;

      const commentsFormServer = await fetchComments(currentPage);

      setItems(commentsFormServer);
      // scroll to the top
      //window.scrollTo(0, 0)
    };


  return (

    <PokecardContainer>
      {pokemonlist.map((pokemon) => ( 
        <Link to={`/details/${getPokemonId(pokemon.url)}`}>     
        <Pokecard key={pokemon.name}>
            <h2>{pokemon.name}</h2>
            <img src={buildImgUrl(pokemon.url)} alt={pokemon.name} />
              <button>Adicionar a pokedex</button>
              <button>Ver detalhes</button>              
        </Pokecard>
        </Link>
      ))}
      <ReactPaginate
       />
    </PokecardContainer>
  );
};

export default Home;
