import React from "react";
import styled from "styled-components";
import {Link} from 'react-router-dom'



const PokecardContainer = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr 1fr 1fr 1fr;
  grid-gap: 10px;
  margin: 10px;
`;

const Pokecard = styled.div`
    /* display: flex;
    flex-direction: column;
    align-items: right;
    justify-content: center;
    max-width: 120px; */
`;

const PokeImage = styled.div`
    background-color: cornflowerblue;
    border-radius: 50%;
    margin: 10px;
    `;

const Home = (props) => {


  return (

    <PokecardContainer>
      {props.pokemonlist.map((pokemon) => ( 

        <div class="nes-container is-rounded"  style={{
          backgroundColor: "red",
          color: "white",
          display: "flex",
          flexDirection: "column",
          ali
        }}>
               <Pokecard key={pokemon.name}>
            <h2>{pokemon.name}</h2>
            <PokeImage>
            <img src={props.buildImgUrl(pokemon.url)} alt={pokemon.name} />
            </PokeImage>
              <button type="button" class="nes-btn is-sucess" onClick={ () => props.addToPokedex(pokemon)}>Add</button>
              <Link to={`/details/${props.getPokemonId(pokemon.url)}`}>
              <button type="button" class="nes-btn is-warning">Detalhes</button>              
              </Link>
        </Pokecard>
            </div>
      ))}

    </PokecardContainer>
  );
};

export default Home;
