import {React, useState} from "react";
import styled from "styled-components";
import './pokedex.css';
import "nes.css/css/nes.min.css"

const PokedexBackground = styled.div`
    display: grid;
    grid-template-columns: 1fr 1fr 1fr 1fr 1fr;
    grid-gap: 10px;
    margin: 10px;
    `;
const backgroundType = (type) => {
        switch (type) {
            case "normal":
                return "lightblue";
            case "fighting":
                return "red";
            case "flying":
                return "lightblue";
            case "poison":
                return "purple";
            case "ground":
                return "brown";
            case "rock":
                return "brown";
            case "bug":
                return "green";
            case "ghost":
                return "purple";
            case "steel":
                return "grey";
            case "fire":
                return "red";
            case "water":
                return "blue";
            case "grass":
                return "green";
            case "electric":
                return "yellow";
            case "psychic":
                return "pink";
            case "ice":
                return "lightblue";
            case "dragon":
                return "purple";
            case "dark":
                return "purple";
            case "fairy":
                return "pink";
            default:
                return "black";
        }
    }

const PokeCardContainer = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: 10px;
    max-width: 120px;
`;

const Pokedex = (props) => {

  return (
    <PokedexBackground>

      {props.pokedex.map((pokemon) => (

                     <div class="nes-container is-rounded">
                <PokeCardContainer
                 key={pokemon.id}
                 style={{
                    backgroundColor: backgroundType(pokemon.types[0].type.name),
                    color: "white",
                  }}>
                    <h2>{pokemon.name}</h2>
                    <img src={props.buildImgUrl(pokemon.url)} alt={pokemon.name} />
                    <button type="button" class="nes-btn is-error" onClick={ () => props.removeFromPokedex(pokemon)}>Remover</button>
                </PokeCardContainer>
                    </div>
            ))}


    </PokedexBackground>
  );
};

export default Pokedex;
