import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import styled from 'styled-components'
import axios from "axios";

const Root = styled.div`
    display: grid;
    height: 100vh;
    border: 1px solid black;
    grid-template-columns: 1fr 1fr 1fr 1fr;
    grid-template-rows: 1fr 1fr 1fr 1fr;
`

const InfoCard = styled.div`
    display: flex;
    flex-direction: column;
    border: 1px solid black;
`

const PokemonDetails = () => {
    const {id} = useParams() 
    const [pokemons, setPokemons] = useState([])


    useEffect (() => {
        axios
            .get(`https://pokeapi.co/api/v2/pokemon/${id}`)
            .then((res) => {
                setPokemons([res.data]);
                console.log(pokemons)
            })
            .catch((err) => {
                console.log(err);
            });
    }, []) 

    const pokemap = pokemons.map((poke) => (
        <Root>       

         <InfoCard>            
         <img src={poke.sprites.front_default}/>
         </InfoCard>
         <InfoCard>
             status
             {/* show pokemon stats */}
             <p>{poke.stats[0].base_stat}</p>
         </InfoCard>
         <InfoCard>
             <p>NÃºmero de tipos: {poke.types.length}</p>

                <p>Tipo 1: {poke.types[0].type.name}</p>
                <p>Tipo 2: {poke.types[1] ? poke.types[1].type.name : 'N/A'}</p>



         </InfoCard>
         <InfoCard>
             movimentos
                {/* show pokemon moves */}
                <p>{poke.moves[0].move.name}</p>

         </InfoCard>
         <InfoCard>
          <img src={poke.sprites.back_default} />
         </InfoCard>

     </Root>
    ))


    return(
        <>
        {pokemap}
        </>
    )

}
export default PokemonDetails
