import { BrowserRouter, Routes, Route } from 'react-router-dom';
import './App.css';
import Home from './components/home/Home';
import PokemonDetails from './components/PokemonDetails/PokemonDetails'
import Header from './components/header/Header';
import Pokedex from './components/pokedex/Pokedex';
import axios from 'axios';
import { useState, useEffect, createContext } from 'react';
import Functions from "../functions/Functions";

export const GlobalContext = createContext()
// GLOBAL STATE SÓ FOI COLOCADO NO HOME

function App() {
  const [pokemonlist, setPokemonlist] = useState([]);
  const [pokedex, setPokedex] = useState([]);

  let limit = 151;

  useEffect(() => {
    getPokemons();
  }, []);

  const getPokemons = () => {
    axios
      .get(`https://pokeapi.co/api/v2/pokemon?limit=${limit}`)
      .then((res) => {
        setPokemonlist(res.data.results);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  // const buildImgUrl = (url) => {
  //   const id = url.split("/");
  //   const idx = id.length - 2;
  //   const imgUrl = `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${id[idx]}.png`;

  //   return imgUrl;
  // };

  return (

    <GlobalContext.Provider value = {{pokemonlist, setPokemonlist, pokedex, setPokedex}}>
      <BrowserRouter>
        <Header />
        <Routes>
          <Route path = "/" element = {<Home
          pokemonlist={pokemonlist}
          // buildImgUrl={buildImgUrl}
          // getPokemonId={getPokemonId}
          // addToPokedex={addToPokedex}
          />} />
          <Route path = "/pokedex" element = {<Pokedex 
          // buildImgUrl={buildImgUrl} 
          pokedex={pokedex}
          // removeFromPokedex={removeFromPokedex}
          pokemonlist={pokemonlist}
          // getPokemonId={getPokemonId} 
          />}
           />          
          <Route path = "/details/:id" element = {<PokemonDetails />} />          
        </Routes>
      </BrowserRouter>
    </GlobalContext.Provider>

  );
}

export default App;
