import React from "react";
import { useEffect, useState} from "react";
import axios from "axios";
import styled from "styled-components";
import {Link} from 'react-router-dom'
import ReactPaginate from "react-paginate";
import AppProvider from '../AppContext/Provider';
import AppContext from '../AppContext/Context';


const PokecardContainer = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr 1fr 1fr 1fr;
`;

const Pokecard = styled.div`
  border: 1px solid black;
  margin: 10px;
  padding: 10px;
  display: flex;
  flex-direction: column;
  gap: 10px;
`;

const Home = () => {
  const [pokemonlist, setPokemonlist] = useState([]);
  const [pageCount, setpageCount] = useState(0);

  let limit = 100;


  useEffect(() => {
    getPokemons();
  }, []);

  const getPokemons = () => {
    axios
      .get(`https://pokeapi.co/api/v2/pokemon?limit=${limit}`)
      .then((res) => {
        setPokemonlist(res.data.results);
        console.log(res.data.results)
        setpageCount(Math.ceil(res.data.count / limit));

      })
      .catch((err) => {
        console.log(err);
      });
  };

  const buildImgUrl = (url) => {
    const id = url.split("/");
    const idx = id.length - 2;
    const imgUrl = `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${id[idx]}.png`;

    return imgUrl;
    };

    const getPokemonId = (url) => {
      const id = url.split("/");
      const idx = id.length - 2;
      const pokemonId = id[idx];
      return pokemonId;
    };

    const handlePageClick = async (pokemonlist) => {
      console.log(pokemonlist.selected);

      let currentPage = pokemonlist.selected + 1;

      const commentsFormServer = await getPokemons(currentPage);

      setPokemonlist(commentsFormServer);
      // scroll to the top
      //window.scrollTo(0, 0)
    };


  return (

    <PokecardContainer>
      {pokemonlist.map((pokemon) => ( 
        <Link to={`/details/${getPokemonId(pokemon.url)}`}>     
        <Pokecard key={pokemon.name}>
            <h2>{pokemon.name}</h2>
            <img src={buildImgUrl(pokemon.url)} alt={pokemon.name} />
              <button>Adicionar a pokedex</button>
              <button>Ver detalhes</button>              
        </Pokecard>
        </Link>
      ))}

      <ReactPaginate
       previousLabel={"previous"}
       nextLabel={"next"}
       breakLabel={"..."}
       pageCount={pageCount}
       marginPagesDisplayed={2}
       pageRangeDisplayed={3}
       onPageChange={handlePageClick}
       containerClassName={"pagination justify-content-center"}
       pageClassName={"page-item"}
       pageLinkClassName={"page-link"}
       previousClassName={"page-item"}
       previousLinkClassName={"page-link"}
       nextClassName={"page-item"}
       nextLinkClassName={"page-link"}
       breakClassName={"page-item"}
       breakLinkClassName={"page-link"}
       activeClassName={"active"}
       />
    </PokecardContainer>
  );
};

export default Home;
