import React from "react";
import styled from "styled-components";
import {Link} from 'react-router-dom'



const PokecardContainer = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr 1fr 1fr 1fr;
  grid-gap: 10px;
  margin: 10px;
`;

const Pokecard = styled.div`
  display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: 10px;
    max-width: 120px;
`;

const PokeImage = styled.div`
    background-color: cornflowerblue;
    border-radius: 50%;
    margin: 10px;
    `;

const Home = (props) => {


  return (

    <PokecardContainer>
      {props.pokemonlist.map((pokemon) => ( 

             <div class="nes-container is-rounded">
        <Pokecard key={pokemon.name}>
            <h2>{pokemon.name}</h2>
            <PokeImage>
            <img src={props.buildImgUrl(pokemon.url)} alt={pokemon.name} />
            </PokeImage>
              <button onClick={ () => props.addToPokedex(pokemon)}>Adicionar a pokedex</button>
              <Link to={`/details/${props.getPokemonId(pokemon.url)}`}>
              <button>Ver detalhes</button>              
              </Link>
        </Pokecard>
            </div>
      ))}

    </PokecardContainer>
  );
};

export default Home;
