import {React, useState} from "react";
import styled from "styled-components";
import './pokedex.css';
import "nes.css/css/nes.min.css"

const PokedexBackground = styled.div``;

const Pokedex = (props) => {

// add function to flip card
const flipCard = (e) => {
    e.target.classList.toggle('flip');
}

  return (
    <PokedexBackground>
      {props.pokedex.map((pokemon) => (
                <div key={pokemon.id}>
                    <h2>{pokemon.name}</h2>
                    <img src={props.buildImgUrl(pokemon.url)} alt={pokemon.name} />
                    <button onClick={ () => props.removeFromPokedex(pokemon)}>Remover</button>

                </div>

            ))}

<a class="nes-btn" href="#">Normal</a>

<button type="button" class="nes-btn is-primary">Primary</button>
<button type="button" class="nes-btn is-success">Success</button>
<button type="button" class="nes-btn is-warning">Warning</button>
<button type="button" class="nes-btn is-error">Error</button>
<button type="button" class="nes-btn is-disabled">Disabled</button>

<label class="nes-btn">
  <span>Select your file</span>
  <input type="file" />
</label>


    </PokedexBackground>
  );
};

export default Pokedex;
