import React from "react";
import { useEffect, useState} from "react";
import axios from "axios";
import styled from "styled-components";
import {Link} from 'react-router-dom'
import ReactPaginate from "react-paginate";
import AppProvider from "../../AppContext/Provider";
import AppContext from "../../AppContext/Context";


const PokecardContainer = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr 1fr 1fr 1fr;
`;

const Pokecard = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  border: 1px solid black;
  margin: 10px;
  padding: 10px;
  display: flex;
  flex-direction: column;
  gap: 10px;
`;

const Home = () => {
  const [pokemonlist, setPokemonlist] = useState([]);

  let limit = 10;


  useEffect(() => {
    getPokemons();
  }, []);

  const getPokemons = () => {
    axios
      .get(`https://pokeapi.co/api/v2/pokemon?limit=${limit}`)
      .then((res) => {
        setPokemonlist(res.data.results);
        console.log(res.data.results)

      })
      .catch((err) => {
        console.log(err);
      });
  };

  const buildImgUrl = (url) => {
    const id = url.split("/");
    const idx = id.length - 2;
    const imgUrl = `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${id[idx]}.png`;

    return imgUrl;
    };

    const getPokemonId = (url) => {
      const id = url.split("/");
      const idx = id.length - 2;
      const pokemonId = id[idx];
      return pokemonId;
    };

    const addToCart = (pokemon) => {
      const pokemonId = getPokemonId(pokemon.url);
      const pokemonName = pokemon.name;
      const pokemonImg = buildImgUrl(pokemon.url);


      const pokemonToAdd = {
        id: pokemonId,
        name: pokemonName,
        img: pokemonImg,
      };

      setUser((currentUser) => ({
        ...currentUser,
        pokedex: {
          ...currentUser.pokedex,
          [pokemonId]: pokemonToAdd
        }
      }));
    };


  return (

    <PokecardContainer>
      {pokemonlist.map((pokemon) => ( 

        <Pokecard key={pokemon.name}>
            <h2>{pokemon.name}</h2>
            <img src={buildImgUrl(pokemon.url)} alt={pokemon.name} />
              <button>Adicionar a pokedex</button>
              <Link to={`/details/${getPokemonId(pokemon.url)}`}>
              <button>Ver detalhes</button>              
              </Link>
        </Pokecard>
      ))}

    </PokecardContainer>
  );
};

export default Home;
